(71, 1, '2018-05-23 12:16:29', '2018-05-23 12:16:29', '&lt;script type=\"text/javascript\"&gt;\r\n\r\njQuery(document).ready(function(){\r\n\r\nvar value = localStorage.getItem(\'hide_cookie_info\');\r\nif (value) {\r\njQuery(\".notification-bar\").remove();\r\n\r\n}\r\nelse {\r\njQuery(\".notification-bar\").show();\r\n\r\n}\r\n\r\njQuery(\'#js_close_bar\').click(function () {\r\n\r\nlocalStorage.setItem(\'hide_cookie_info\', true);\r\njQuery(\'.notification-bar\').remove();\r\n\r\n});\r\n});\r\n\r\n&lt;/script&gt;\r\n&lt;div class=\"notification-bar\" style=\"display:none\"&gt;\r\n&lt;div class=\"notification-bar__content\"&gt;\r\n&lt;button id=\"js_close_bar\" role=\"button\"&gt; + &lt;/button&gt;\r\n&lt;p&gt;Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.  &lt;a href=\"#\" class=\"btn-link\"&gt;&lt;u&gt;Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.&lt;/u&gt;&lt;/a&gt;.&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;', 'Cookies Add/Remove', '', 'inherit', 'closed', 'closed', '', '70-revision-v1', '', '', '2018-05-23 12:16:29', '2018-05-23 12:16:29', '', 70, 'http://ayushshrestha.com.np/blog/index.php/2018/05/23/70-revision-v1/', 0, 'revision', '', 0),
