(68, 1, '2018-05-09 01:57:22', '2018-05-09 01:57:22', '@-webkit-keyframes kenBurns {\r\n0% {\r\ntransform: scale(1);\r\n}\r\n100% {\r\ntransform: scale(1.1);\r\n}\r\n}\r\n\r\n.[wrapper for image]{\r\noverflow: hidden;\r\nbackface-visibility: hidden;\r\n}\r\n\r\nimg {\r\ndisplay: block;\r\nanimation: kenBurns 15s ease alternate infinite;\r\n-webkit-animation: kenBurns 15s ease alternate infinite;\r\n}\r\n\r\n&nbsp;\r\n\r\nDemo : https://www.holyweed.ch\r\n\r\n&nbsp;', 'kenBurns CSS Effects', '', 'inherit', 'closed', 'closed', '', '66-revision-v1', '', '', '2018-05-09 01:57:22', '2018-05-09 01:57:22', '', 66, 'http://ayushshrestha.com.np/blog/index.php/2018/05/09/66-revision-v1/', 0, 'revision', '', 0),
(66, 1, '2018-05-09 01:57:22', '2018-05-09 01:57:22', '@-webkit-keyframes kenBurns {\n0% {\ntransform: scale(1);\n}\n100% {\ntransform: scale(1.1);\n}\n}\n\n.[wrapper for image]{\noverflow: hidden;\nbackface-visibility: hidden;\n}\n\nimg {\ndisplay: block;\nanimation: kenBurns 15s ease alternate infinite;\n-webkit-animation: kenBurns 15s ease alternate infinite;\n}\n\n&nbsp;\n\nDemo :&nbsp;https://www.holyweed.ch\n\n&nbsp;', 'kenBurns CSS Effects', '', 'publish', 'open', 'open', '', 'kenburns-css-effects', '', '', '2023-05-04 05:17:23', '2023-05-04 05:17:23', '', 0, 'http://ayushshrestha.com.np/blog/?p=66', 0, 'post', '', 0),
(55, 1, '2018-03-20 10:48:04', '2018-03-20 10:48:04', 'As the title says, a simple Google Map with get directions feature.\r\n\r\nSimply change the Lat and Long and address and you’re good to go.\r\n\r\n&nbsp;\r\n\r\n<a href=\"https://jsfiddle.net/ZombieBubbleGum/5g6h5qbz/\">DEMO</a>\r\n<pre>&lt;script type=\"text/javascript\" src=\"http://maps.google.com/maps/api/js?sensor=false\"&gt;&lt;/script&gt;\r\n&lt;script type=\"text/javascript\"&gt;\r\n //&lt;![CDATA[\r\n var directionDisplay;\r\n var directionsService = new google.maps.DirectionsService();\r\n var map;\r\n var panorama;\r\n \r\n //CHANGE LAT LONG TO THE LOCATION YOU\'D LIKE TO SHOW\r\n var latLng = new google.maps.LatLng(51.672126, 0.257473);\r\n function initialize() {\r\n directionsDisplay = new google.maps.DirectionsRenderer();\r\n \r\n var myOptions = {\r\n center: latLng,\r\n zoom: 13,\r\n streetViewControl: false,\r\n mapTypeId: google.maps.MapTypeId.ROADMAP\r\n }\r\n \r\n map = new google.maps.Map(document.getElementById(\"map-canvas\"), myOptions);\r\n directionsDisplay.setMap(map);\r\n directionsDisplay.setPanel(document.getElementById(\"directionsPanel\"));\r\n \r\n panorama = map.getStreetView();\r\n panorama.setPosition(latLng);\r\n panorama.setPov({\r\n heading: 125, /* SPIN STREET VIEW */\r\n zoom:1,\r\n pitch:0}\r\n );\r\n }\r\n function openWindow(){\r\n var yourMarker = new google.maps.Marker({\r\n position: latLng,\r\n map: map\r\n });\r\n //CHANGE THESE DETAILS TO THE ADDRESS YOU LIKE TO SHOW\r\n //NOTE, THIS ADDRESS IS FOR DISPLAY ONLY, THIS DOESN\'T MODIFY THE MAPS LOCATION\r\n var content = \'&lt;p style=\"color:#666; margin:0; padding:0; line-height:normal;\"&gt;&lt;strong&gt;Secret Nuclear Bunker&lt;/strong&gt;&lt;br/&gt;Kelvedon Hall Ln,&lt;br/&gt;Kelvedon Hatch,&lt;br/&gt;Brentwood,&lt;br/&gt;Essex&lt;br/&gt;CM14 5TL&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;Tel&lt;/strong&gt;: 01277 364 883&lt;br/&gt;&lt;strong&gt;URL&lt;/strong&gt;: &lt;a href=\"http://www.secretnuclearbunker.com\" target=\"_blank\"&gt;secretnuclearbunker.com&lt;/a&gt;&lt;br/&gt;&lt;/p&gt;&lt;br /&gt;&lt;input type=\"button\" name=\"street\" class=\"street\" value=\"Street View\" onclick=\"toggleStreetView();\" /&gt;&lt;div class=\"clearfix\"&gt;&lt;/div&gt;\';\r\n var infowindow = new google.maps.InfoWindow({\r\n content: content\r\n\r\n\r\n });\r\n google.maps.event.addListener(yourMarker, \'click\', function() {\r\n infowindow.open(map, yourMarker);\r\n });\r\n infowindow.open(map, yourMarker);\r\n }\r\n function calcRoute() {\r\n var start = document.getElementById(\"postcode\").value;\r\n var request = {\r\n origin:start, \r\n destination:latLng,\r\n travelMode: google.maps.DirectionsTravelMode.DRIVING,\r\n unitSystem: google.maps.UnitSystem.IMPERIAL\r\n };\r\n directionsService.route(request, function(response, status) {\r\n if (status == google.maps.DirectionsStatus.OK) {\r\n directionsDisplay.setDirections(response);\r\n }\r\n });\r\n }\r\n function toggleStreetView() {\r\n var toggle = panorama.getVisible();\r\n if (toggle == false) {\r\n panorama.setVisible(true);\r\n } else {\r\n panorama.setVisible(false);\r\n }\r\n }\r\n window.onload = initialize;\r\n setTimeout(\"openWindow()\",1000);\r\n //]]&gt;\r\n&lt;/script&gt;\r\n\r\n&lt;script type=\"text/javascript\"&gt;window.onload=function(){initialize();}&lt;/script&gt;\r\n\r\n&lt;div id=\"map-canvas\" class=\"t1_footer_map\" style=\"width:100%;height:500px\"&gt;&lt;/div&gt;\r\n\r\n&lt;hr/&gt;\r\n\r\n&lt;div class=\"col-xs-12 col-sm-6\"&gt;&lt;input id=\"postcode\" class=\"form-control\" name=\"postcode\" type=\"text\" placeholder=\"Enter Postcode...\" value=\"\" /&gt;&lt;/div&gt;\r\n\r\n&lt;div class=\"col-xs-12 col-sm-6\"&gt;&lt;input type=\"button\" class=\"btn btn-default\" value=\"Get Directions\" onclick=\"calcRoute();\" /&gt;&lt;/div&gt;\r\n\r\n&lt;div class=\"clearfix\"&gt;&lt;/div&gt;\r\n\r\n&lt;hr/&gt;\r\n\r\n&lt;div id=\"directionsPanel\" class=\"mapdir\"&gt;&lt;/div&gt;</pre>', 'Bootstrap Google Map with get directions feature (Google Map API)', '', 'publish', 'open', 'open', '', 'bootstrap-google-map-with-get-directions-feature-google-map-api', '', '', '2018-03-20 10:48:04', '2018-03-20 10:48:04', '', 0, 'http://ayushshrestha.com.np/blog/?p=55', 0, 'post', '', 0),
