          <div class=\"swiper-slide w-100\">\n                                            <div class=\"image image-3by2 overflow-hidden w-100\">\n                                                <figure>\n                                                    <a href=\"\" onclick=\"return false;\">\n                                                        <img src=\"\">\n                                                    </a>\n                                                </figure>\n                                            </div>\n                                        </div>\n                                        <div class=\"swiper-slide w-100\">\n                                            <div class=\"image image-3by2 overflow-hidden w-100\">\n                                                <figure>\n                                                    <a href=\"\" onclick=\"return false;\">\n                                                        <img src=\"\">\n                                                    </a>\n                                                </figure>\n                                            </div>\n                                        </div>\n                                </div>\n\n                                    <!-- If we need navigation buttons -->\n                                    <div class=\"swiper-button-prev\"></div>\n                                    <div class=\"swiper-button-next\"></div>\n                                    <div class=\"swiper-pagination swiper-pagination-md bottom-0 d-none\"></div>\n                                    <div class=\"counter\">\n                                        <span class=\"slide-current\">1</span>/<span class=\"slide-total\"></span>\n                                    </div>\n                            </div>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Custom JS</strong><br><br>let largeSliders = document.querySelectorAll(\'.swiperSimple\')<br>let prevArrow = document.querySelectorAll(\'.swiper-button-prev\')<br>let nextArrow = document.querySelectorAll(\'.swiper-button-next\')<br>let swiperSlide = document.querySelectorAll(\'.swiper-slide\')<br>let slideCurrent = document.querySelectorAll(\'.slide-current\')<br>let slideTotal = document.querySelectorAll(\'.slide-total\')<br>largeSliders.forEach((slider, index) =&gt; {<br>let sliderLength = slider.children[0].children.length<br>let numOfSlides = sliderLength;<br>//let result = (sliderLength &gt; 1) ? true : false<br>var swiper = new Swiper(slider, {<br>//loop: result,<br>navigation: {<br>prevEl: prevArrow[index],<br>nextEl: nextArrow[index],<br>},<br>on: {<br>slideChange: function () {<br>var currentSlide = this.realIndex + 1;<br>slideCurrent[index].innerHTML = currentSlide;<br>},<br>beforeInit: function () {<br>slideTotal[index].innerHTML = numOfSlides;<br>}<br>}<br>});<br>})</p>\n<!-- /wp:paragraph -->', 'Multiple Swiper/Slick Slider with Count', '', 'inherit', 'closed', 'closed', '', '323-revision-v1', '', '', '2021-11-26 08:31:32', '2021-11-26 08:31:32', '', 323, 'https://www.ayushshrestha.com.np/?p=330', 0, 'revision', '', 0),
