rchical\";b:0;s:19:\"exclude_from_search\";b:0;s:18:\"publicly_queryable\";b:1;s:7:\"show_ui\";b:1;s:12:\"show_in_menu\";b:1;s:17:\"admin_menu_parent\";s:0:\"\";s:17:\"show_in_admin_bar\";b:1;s:17:\"show_in_nav_menus\";b:1;s:12:\"show_in_rest\";b:1;s:9:\"rest_base\";s:0:\"\";s:14:\"rest_namespace\";s:5:\"wp/v2\";s:21:\"rest_controller_class\";s:24:\"WP_REST_Posts_Controller\";s:13:\"menu_position\";i:10;s:9:\"menu_icon\";s:21:\"dashicons-media-video\";s:19:\"rename_capabilities\";b:0;s:24:\"singular_capability_name\";s:4:\"post\";s:22:\"plural_capability_name\";s:5:\"posts\";s:8:\"supports\";a:10:{i:0;s:5:\"title\";i:1;s:6:\"author\";i:2;s:8:\"comments\";i:3;s:10:\"trackbacks\";i:4;s:6:\"editor\";i:5;s:7:\"excerpt\";i:6;s:9:\"revisions\";i:7;s:15:\"page-attributes\";i:8;s:9:\"thumbnail\";i:9;s:13:\"custom-fields\";}s:10:\"taxonomies\";a:3:{i:0;s:8:\"category\";i:1;s:11:\"post_format\";i:2;s:8:\"post_tag\";}s:11:\"has_archive\";b:0;s:16:\"has_archive_slug\";s:0:\"\";s:7:\"rewrite\";a:4:{s:17:\"permalink_rewrite\";s:13:\"post_type_key\";s:10:\"with_front\";s:1:\"1\";s:5:\"feeds\";s:1:\"0\";s:5:\"pages\";s:1:\"1\";}s:9:\"query_var\";s:13:\"post_type_key\";s:14:\"query_var_name\";s:0:\"\";s:10:\"can_export\";b:1;s:16:\"delete_with_user\";b:0;s:20:\"register_meta_box_cb\";s:0:\"\";}', 'Entrepreneurs', 'entrepreneurs', 'trash', 'closed', 'closed', '', 'post_type_64b621e36b03c__trashed', '', '', '2023-07-18 06:12:55', '2023-07-18 06:12:55', '', 0, 'http://localhost/personal/ayushshrestha.com.np/?post_type=acf-post-type&#038;p=627', 0, 'acf-post-type', '', 0),
