(122, 1, '2019-02-09 07:06:07', '2019-02-09 07:06:07', '<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>&lt;script class=\"jsbin\" src=\"https://ajax.googleapis.com/ajax/libs/jquery/1/jquery.min.js\"&gt;&lt;/script&gt;\n&lt;div class=\"file-upload\"&gt;\n  &lt;button class=\"file-upload-btn\" type=\"button\" onclick=\"$(\'.file-upload-input\').trigger( \'click\' )\"&gt;Add Image&lt;/button&gt;\n\n  &lt;div class=\"image-upload-wrap\"&gt;\n    &lt;input class=\"file-upload-input\" type=\'file\' onchange=\"readURL(this);\" accept=\"image/*\" /&gt;\n    &lt;div class=\"drag-text\"&gt;\n      &lt;h3&gt;Drag and drop a file or select add Image&lt;/h3&gt;\n    &lt;/div&gt;\n  &lt;/div&gt;\n  &lt;div class=\"file-upload-content\"&gt;\n    &lt;img class=\"file-upload-image\" src=\"#\" alt=\"your image\" /&gt;\n    &lt;div class=\"image-title-wrap\"&gt;\n      &lt;button type=\"button\" onclick=\"removeUpload()\" class=\"remove-image\"&gt;Remove &lt;span class=\"image-title\"&gt;Uploaded Image&lt;/span&gt;&lt;/button&gt;\n    &lt;/div&gt;\n  &lt;/div&gt;\n&lt;/div&gt;\n\n\n\n////////////////////////////// STYLE CSS ///////////////////////////////////////////\nbody {\n  font-family: sans-serif;\n  background-color: #eeeeee;\n}\n\n.file-upload {\n  background-color: #ffffff;\n  width: 600px;\n  margin: 0 auto;\n  padding: 20px;\n}\n\n.file-upload-btn {\n  width: 100%;\n  margin: 0;\n  color: #fff;\n  background: #1FB264;\n  border: none;\n  padding: 10px;\n  border-radius: 4px;\n  border-bottom: 4px solid #15824B;\n  transition: all .2s ease;\n  outline: none;\n  text-transform: uppercase;\n  font-weight: 700;\n}\n\n.file-upload-btn:hover {\n  background: #1AA059;\n  color: #ffffff;\n  transition: all .2s ease;\n  cursor: pointer;\n}\n\n.file-upload-btn:active {\n  border: 0;\n  transition: all .2s ease;\n}\n\n.file-upload-content {\n  display: none;\n  text-align: center;\n}\n\n.file-upload-input {\n  position: absolute;\n  margin: 0;\n  padding: 0;\n  width: 100%;\n  height: 100%;\n  outline: none;\n  opacity: 0;\n  cursor: pointer;\n}\n\n.image-upload-wrap {\n  margin-top: 20px;\n  border: 4px dashed #1FB264;\n  position: relative;\n}\n\n.image-dropping,\n.image-upload-wrap:hover {\n  background-color: #1FB264;\n  border: 4px dashed #ffffff;\n}\n\n.image-title-wrap {\n  padding: 0 15px 15px 15px;\n  color: #222;\n}\n\n.drag-text {\n  text-align: center;\n}\n\n.drag-text h3 {\n  font-weight: 100;\n  text-transform: uppercase;\n  color: #15824B;\n  padding: 60px 0;\n}\n\n.file-upload-image {\n  max-height: 200px;\n  max-width: 200px;\n  margin: auto;\n  padding: 20px;\n}\n\n.remove-image {\n  width: 200px;\n  margin: 0;\n  color: #fff;\n  background: #cd4535;\n  border: none;\n  padding: 10px;\n  border-radius: 4px;\n  border-bottom: 4px solid #b02818;\n  transition: all .2s ease;\n  outline: none;\n  text-transform: uppercase;\n  font-weight: 700;\n}\n\n.remove-image:hover {\n  background: #c13b2a;\n  color: #ffffff;\n  transition: all .2s ease;\n  cursor: pointer;\n}\n\n.remove-image:active {\n  border: 0;\n  transition: all .2s ease;\n}\n\n\n////////////////////////////////////////////// CUSTOM JS ////////////////////////////////\n\nfunction readURL(input) {\n  if (input.files &amp;&amp; input.files&#91;0]) {\n\n    var reader = new FileReader();\n\n    reader.onload = function(e) {\n      $(\'.image-upload-wrap\').hide();\n\n      $(\'.file-upload-image\').attr(\'src\', e.target.result);\n      $(\'.file-upload-content\').show();\n\n      $(\'.image-title\').html(input.files&#91;0].name);\n    };\n\n    reader.readAsDataURL(input.files&#91;0]);\n\n  } else {\n    removeUpload();\n  }\n}\n\nfunction removeUpload() {\n  $(\'.file-upload-input\').replaceWith($(\'.file-upload-input\').clone());\n  $(\'.file-upload-content\').hide();\n  $(\'.image-upload-wrap\').show();\n}\n$(\'.image-upload-wrap\').bind(\'dragover\', function () {\n		$(\'.image-upload-wrap\').addClass(\'image-dropping\');\n	});\n	$(\'.image-upload-wrap\').bind(\'dragleave\', function () {\n		$(\'.image-upload-wrap\').removeClass(\'image-dropping\');\n});\n</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":141} -->\n<figure class=\"wp-block-image\"><img src=\"http://ayushshrestha.com.np/blog/wp-content/uploads/2019/02/file-upload-input.png\" alt=\"Drag and Drop image \nFile Select add image\" class=\"wp-image-141\"/></figure>\n<!-- /wp:image -->', 'File Upload Input', '', 'publish', 'open', 'open', '', 'file-upload-input', '', '', '2023-05-04 05:17:11', '2023-05-04 05:17:11', '', 0, 'http://ayushshrestha.com.np/blog/?p=122', 0, 'post', '', 0),
